// 他キーも同様に定数化

private void InitializeComponent()
{
    this.components = new System.ComponentModel.Container();

    SuspendLayout();
    ((System.ComponentModel.ISupportInitialize)(this.ultraToolbarsManager1)).BeginInit();
    ((System.ComponentModel.ISupportInitialize)(this.ultraStatusBar1)).BeginInit();

    try
    {
        InitializeToolbars();
        InitializeStatusBar();
        InitializeLayout();
    }
    finally
    {
        ((System.ComponentModel.ISupportInitialize)(this.ultraStatusBar1)).EndInit();
        ((System.ComponentModel.ISupportInitialize)(this.ultraToolbarsManager1)).EndInit();
        ResumeLayout(false);
        PerformLayout();
    }
}

private void InitializeToolbars()
{
    Infragistics.Win.UltraWinToolbars.UltraToolbar ultraToolbar1 =
