    this.stsInfo.SuspendLayout();
    this.spltMain.SuspendLayout();
    this.spltMain.Panel1.SuspendLayout();
    this.spltMain.Panel2.SuspendLayout();

    this.spltCategory.SuspendLayout();
    this.spltCategory.Panel1.SuspendLayout();
    this.spltCategory.Panel2.SuspendLayout();

    this.pnlCategory.SuspendLayout();
    this.pnlSubCategory.SuspendLayout();
    this.mnuMain.SuspendLayout();
    this.barCtrl.SuspendLayout();
    this.barLoginInfo.SuspendLayout();
    this.barInfo.SuspendLayout();
    this.barFuncMenu.SuspendLayout();

    // …従来のコントロール初期化とプロパティ設定…

    // レイアウト再開
    this.barFuncMenu.ResumeLayout(false);
    this.barInfo.ResumeLayout(false);
    this.barLoginInfo.ResumeLayout(false);
    this.barCtrl.ResumeLayout(false);
    this.mnuMain.ResumeLayout(false);
    this.pnlSubCategory.ResumeLayout(false);
    this.pnlCategory.ResumeLayout(false);
    this.spltCategory.Panel2.ResumeLayout(false);
    this.spltCategory.Panel1.ResumeLayout(false);
    this.spltCategory.ResumeLayout(false);
    this.spltMain.Panel2.ResumeLayout(false);
    this.spltMain.Panel1.ResumeLayout(false);
    this.spltMain.ResumeLayout(false);
    this.stsInfo.ResumeLayout(false);
    this.tscMain.BottomToolStripPanel.ResumeLayout(false);
    this.tscMain.BottomToolStripPanel.PerformLayout();
    this.tscMain.TopToolStripPanel.ResumeLayout(false);
    this.tscMain.TopToolStripPanel.PerformLayout();
    this.tscMain.ResumeLayout(false);
    this.tscMain.PerformLayout();
    this.ResumeLayout(false);
}
